const { Listener } = require('discord-akairo');
const Logger = require('../../util/Logger');

class CommandBlockedListener extends Listener {
	constructor() {
		super('commandBlocked', {
			event: 'commandBlocked',
			emitter: 'commandHandler',
			category: 'commandHandler'
		});
	}

	exec(message, command, reason) {
		const text = {
			owner: () => 'You must be the owner to use this command.',
			guild: () => 'You must be in a guild to use this command.'
		}[reason];

		const tag = message.guild ? `${message.guild.name} :: ${message.author.tag} (${message.author.id})` : `${message.author.tag} (${message.author.id})`;
		Logger.log(`=> ${command.id} ~ ${reason}`, { tag });

		if (!text) return;
		if (message.guild ? message.channel.permissionsFor(this.client.user).has('SEND_MESSAGES') : true) {
			message.reply(text());
		}
	}
}

module.exports = CommandBlockedListener;

commandStarted.content:
const { Listener } = require('discord-akairo');
const Logger = require('../../util/Logger');
const Raven = require('raven');
const Levels = require('../../models/UserLevel');
const Commands = require('../../models/Commands');

class CommandStartedListener extends Listener {
	constructor() {
		super('commandStarted', {
			event: 'commandStarted',
			emitter: 'commandHandler',
			category: 'commandHandler'
		});
	}

	async exec(message, command, args) {
		this.client.prometheus.commandCounter.inc();

		if (message.guild && command.categoryID && command.categoryID !== 'owner') {
			const _command = await Commands.findOne({ where: { guildID: message.guild.id, commandID: command.id }});
			if (_command) {
				await Commands.update({
					uses: _command.uses + 1, categoryID: command.categoryID,
					commandAlias: command.aliases ? command.aliases[0] : command.id
				}, { where: { guildID: message.guild.id, commandID: command.id }});

			} else await Commands.create({
				commandID: command.id, guildID: message.guild.id, uses: 1, categoryID: command.categoryID,
				commandAlias: command.aliases ? command.aliases[0] : command.id
			});

			if (command.id === 'tag-show') {
				const _user = await Levels.findOne({ where: { guildID: message.guild.id, userID: message.author.id }});
				if (_user) {
					await Levels.update({
						tagUses: _user.tagUses + 1
					}, { where: { guildID: message.guild.id, userID: message.author.id }});

				} else await Levels.create({ guildID: message.guild.id, userID: message.author.id, tagUses: 1 });

			} else if (command.categoryID !== 'tags') {
				const _user = await Levels.findOne({ where: { guildID: message.guild.id, userID: message.author.id }});
				if (_user) {
					await Levels.update({
						commandUses: _user.commandUses + 1
					}, { where: { guildID: message.guild.id, userID: message.author.id }});

				} else await Levels.create({ guildID: message.guild.id, userID: message.author.id, commandUses: 1 });
			}
		}

		const tag = message.guild ? `${message.guild.name} - ${message.author.tag}` : `${message.author.tag}`;
		Logger.log(`=> ${command.id}`, { tag });

		Raven.captureBreadcrumb({
			message: 'command_started',
			category: command.category.id,
			data: {
				user: {
					id: message.author.id,
					username: message.author.tag
				},
				guild: message.guild ? {
					id: message.guild.id,
					name: message.guild.name
				} : null,
				command: {
					id: command.id,
					aliases: command.aliases,
					category: command.category.id
				},
				message: {
					id: message.id,
					content: message.content
				},
				args
			}
		});
		Raven.setContext({
			user: {
				id: message.author.id,
				username: message.author.tag
			},
			extra: {
				guild: message.guild ? {
					id: message.guild.id,
					name: message.guild.name
				} : null,
				command: {
					id: command.id,
					aliases: command.aliases,
					category: command.category.id
				},
				message: {
					id: message.id,
					content: message.content
				},
				args
			}
		});
	}
}

module.exports = CommandStartedListener;

cooldown.content:
const { Listener } = require('discord-akairo');
const Logger = require('../../util/Logger');

class CooldownListener extends Listener {
	constructor() {
		super('cooldown', {
			event: 'cooldown',
			emitter: 'commandHandler',
			category: 'commandHandler'
		});
	}

	exec(message, command, remaining) {
		const time = remaining / 1000;
		const tag = message.guild ? `${message.guild.name} :: ${message.author.tag} (${message.author.id})` : `${message.author.tag} (${message.author.id})`;
		Logger.log(`=> ${command.id} ~ ${time}`, { tag });

		if (message.guild ? message.channel.permissionsFor(this.client.user).has('SEND_MESSAGES') : true) {
			message.reply(`You can use that command again in ${time} seconds.`);
		}
	}
}

module.exports = CooldownListener;

error.content:
const { Listener } = require('discord-akairo');
const Logger = require('../../util/Logger');
const Raven = require('raven');

class ErrorListener extends Listener {
	constructor() {
		super('error', {
			event: 'error',
			emitter: 'commandHandler',
			category: 'commandHandler'
		});
	}

	async exec(error, message, command) {

		Logger.error('An error occured in a command.');

		const tag = message.guild ? `${message.guild.name} :: ${message.author.tag} (${message.author.id})` : `${message.author.tag} (${message.author.id})`;
		Logger.error(message.content, { tag });
		Logger.stacktrace(error);

		Raven.captureBreadcrumb({
			message: 'command_errored',
			category: command ? command.category.id : 'inhibitor',
			data: {
				user: {
					id: message.author.id,
					username: message.author.tag
				},
				guild: message.guild ? {
					id: message.guild.id,
					name: message.guild.name
				} : null,
				command: command ? {
					id: command.id,
					aliases: command.aliases,
					category: command.category.id
				} : null,
				message: {
					id: message.id,
					content: message.content
				}
			}
		});
		Raven.captureException(error);

		if (message.guild ? message.channel.permissionsFor(this.client.user).has('SEND_MESSAGES') : true) {
			const owner = this.client.users.get(this.client.ownerID).tag;
			message.channel.send([
				'```js',
				error.toString(),
				'```'
			]);
		}
	}
}

module.exports = ErrorListener;

