const { Listener } = require('discord-akairo');

class MessageInvalidListener extends Listener {
	constructor() {
		super('messageInvalid', {
			emitter: 'commandHandler',
			event: 'messageInvalid',
			category: 'commandHandler'
		});
	}

	async exec(message) {
		if (message.guild && message.util.parsed.prefix) {
			if (!message.util.parsed.alias || !message.util.parsed.afterPrefix) return;
			const command = this.client.commandHandler.modules.get('tag-show');
			
			return this.client.commandHandler.runCommand(message, command, await command.parse(message, message.util.parsed.afterPrefix));
		}
	}
}

module.exports = MessageInvalidListener;

missingPermissions.content:
const { Listener } = require('discord-akairo');
const Logger = require('../../util/Logger');

class MissingPermissionsListener extends Listener {
	constructor() {
		super('missingPermissions', {
			event: 'missingPermissions',
			emitter: 'commandHandler',
			category: 'commandHandler'
		});
	}

	exec(message, command, type, missing) {
		const text = {
			client: () => {
				const str = this.missingPermissions(message.channel, this.client.user, missing);
				return `I'm missing ${str} to use that command.`;
			},
			user: () => {
				const str = this.missingPermissions(message.channel, message.author, missing);
				return `You are missing ${str === undefined ? 'moderator permission' : str} to use that command.`;
			}
		}[type];

		const tag = message.guild ? `${message.guild.name} :: ${message.author.tag} (${message.author.id})` : `${message.author.tag} (${message.author.id})`;
		Logger.log(`=> ${command.id} ~ ${type}Permissions`, { tag });

		if (!text) return;
		if (message.guild ? message.channel.permissionsFor(this.client.user).has('SEND_MESSAGES') : true) {
			message.reply(text());
		}
	}

	missingPermissions(channel, user, permissions) {
		const missingPerms = channel.permissionsFor(user).missing(permissions)
			.map(str => {
				if (str === 'VIEW_CHANNEL') return '`Read Messages`';
				if (str === 'SEND_TTS_MESSAGES') return '`Send TTS Messages`';
				if (str === 'USE_VAD') return '`Use VAD`';
				return `\`${str.replace(/_/g, ' ').toLowerCase().replace(/\b(\w)/g, char => char.toUpperCase())}\``;
			});

		return missingPerms.length > 1
			? `${missingPerms.slice(0, -1).join(', ')} and ${missingPerms.slice(-1)[0]}`
			: missingPerms[0];
	}
}

module.exports = MissingPermissionsListener;

blacklist.content:
const { Inhibitor } = require('discord-akairo');

class BlacklistInhibitor extends Inhibitor {
	constructor() {
		super('blacklist', {
			reason: 'blacklist'
		});
	}

	exec(message) {
		const blacklist = this.client.settings.get('global', 'blacklist', []);
		return blacklist.includes(message.author.id);
	}
}

module.exports = BlacklistInhibitor;

moderation.content:
const { Inhibitor } = require('discord-akairo');

class ModerationInhibitor extends Inhibitor {
	constructor() {
		super('moderation', {
			reason: 'moderation'
		});
	}

	exec(message) {
		if (message.util.parsed && message.util.parsed.command && message.util.parsed.command.categoryID !== 'mod') return false;
		if (!this.client.settings.get(message.guild, 'moderation', undefined)) {
			return true;
		}
		return false;
	}
}

module.exports = ModerationInhibitor;

sendMessages.content:
const { Inhibitor } = require('discord-akairo');

class SendMessagesInhibitor extends Inhibitor {
	constructor() {
		super('sendMessages', { reason: 'sendMessages' });
	}

	exec(message, command) {
		if (!message.guild) return false;
		if (command.id === 'help') return false;
		return !message.channel.permissionsFor(this.client.user).has('SEND_MESSAGES');
	}
}

module.exports = SendMessagesInhibitor;
